create table "expense" ("id" INTEGER GENERATED BY DEFAULT AS IDENTITY(START WITH 1) NOT NULL PRIMARY KEY,"amount" DOUBLE NOT NULL,"date" DATE NOT NULL,"description" VARCHAR(254) DEFAULT 'miscellaneous' NOT NULL);
create table "tag" ("id" INTEGER GENERATED BY DEFAULT AS IDENTITY(START WITH 1) NOT NULL PRIMARY KEY,"name" VARCHAR(254) NOT NULL);
create table "expense_tag" ("id" INTEGER GENERATED BY DEFAULT AS IDENTITY(START WITH 1) NOT NULL PRIMARY KEY,"expense" INTEGER NOT NULL,"tag" INTEGER NOT NULL);
alter table "expense_tag" add constraint "expense_with_tag_expense_fk" foreign key("expense") references "expense"("id") on update NO ACTION on delete NO ACTION;
alter table "expense_tag" add constraint "expense_with_tag_tag_fk" foreign key("tag") references "tag"("id") on update NO ACTION on delete NO ACTION;